function initializeScheduleSwitcher(a){a.ScheduleSwitcher=Backbone.View.extend({master:null,schedule:null,rivets:null,initialize:function(b){this.master=b.master;this.schedule=new a.Schedule(b.schedule);this.rivets=rivets.bind(this.el,{schedule:this.schedule});this.$el.removeClass("hidden")},changeDay:function(b){_.each(this.schedule.get("workDays"),function(c){if(c.get("dayId")==b.getDay()){c.set("current",true)}else{c.set("current",false)}})}})};